import React, { useState } from 'react';
import Square from '../components/Square';
import '../css/GameComponents.css'
import { calculateWinner } from './GameUtils';

const Game = () => {
  const [board, setBoard] = useState(Array(9).fill(null))
  const [countStep, setCount] = useState(0)
  const winner = calculateWinner(board)


  function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min)) + min; //Максимум не включается, минимум включается
  }

  const buttonClick = (index) => {
    const boardCopy = [...board]
    let step = 0;

    if (winner || boardCopy[index]) return;
    boardCopy[index] = 'X'
    step++

    winner = calculateWinner(board)
    if (winner === null && countStep < 8) {
      while (boardCopy[index]) {
        index = getRandomInt(0, 9);
      }
      boardCopy[index] = 'O'
      step++
    }

    setCount(countStep + step)
    setBoard(boardCopy)
  }

  const startNewGame = () => {
    return (
        <button variant={"outline-light"} className="start__btn" onClick={() => setBoard(Array(9).fill(null))}> NEW GAME</button>
    )
}
  return (
    <div className='black_back'>
      { startNewGame() }
      <div className="board">
        {
          board.map((square, i) => (
            <Square
              key={i}
              value={square}
              onClick={() => buttonClick(i)} />
          ))
        }
      </div>
      <div className='game__info'> {winner ? "Победил " + winner + " !": "Ходит: X"}</div>
    </div>
  );
}

export default Game;
